INSERT INTO `hellomoney`.`review` (`review_id`, `request_id`, `content`, `score`, `register_time`) VALUES ('1', '1', '비오는 날인데도 잘찾아 와주시고 너무나 친절히 설명도 잘해주셔서 정말 만족합니다 다음 대출할때도 꼭 다시 이용할꺼 같아요~', '10', '');
INSERT INTO `hellomoney`.`review` (`review_id`, `request_id`, `content`, `score`, `register_time`) VALUES ('2', '2', '금리도, 서비스도 모두만족스러웠어요. 아주아주 만족ㅎㅎ 기분좋게 진행해주셔서 감사해요. 꾸벅~~^^', '8', '');
INSERT INTO `hellomoney`.`review` (`review_id`, `request_id`, `content`, `score`, `register_time`) VALUES ('3', '3', '견적 그대로 깔끔하게 진행했네요. 걱정도 많이 했는데 인상도 좋으시고 친절하게 진행해 주셔서 기분좋게 마무리했습니다~', '10', '');
INSERT INTO `hellomoney`.`review` (`review_id`, `request_id`, `content`, `score`, `register_time`) VALUES ('4', '4', '대출받게되서 여러가지 걱정이 많았는데 약속시간도 정확하게 지키셔서 진행하면서 기분이 무척 좋았습니다. 주위에 추천드리고 싶은 상담사님이세요. 감사합니다.', '8', '');
INSERT INTO `hellomoney`.`review` (`review_id`, `request_id`, `content`, `score`, `register_time`) VALUES ('5', '5', '먼저 친절한 응대에 감사 드립니다. 좋은 금리 내어 주시고 멀리서 오셨음에도 밝은 얼굴 참 보기 좋았습니다. 감사합니다.', '8', '');
INSERT INTO `hellomoney`.`review` (`review_id`, `request_id`, `content`, `score`, `register_time`) VALUES ('6', '6', '친절하고 꼼꼼하게 설명해주시고 좋은 금리 ... 확실한 일처리 감사합니다 항상 행복하세요~^^', '10', '');
INSERT INTO `hellomoney`.`review` (`review_id`, `request_id`, `content`, `score`, `register_time`) VALUES ('7', '7', '인사말과 마찬가지로 확실한 일처리와 낮은 금리, 친절한 미소 감사합니다^^이런 좋은 어플이 생겨서 좋은 분에게 기분좋게 대출하게 되어 좋네요!!', '10', '');
INSERT INTO `hellomoney`.`review` (`review_id`, `request_id`, `content`, `score`, `register_time`) VALUES ('8', '8', '처음만났음에도 친절하게 맞아주시고 대출에 대해서도 쉽게 설명해주셨습니다 또한 등기까지 확실히 해주신것 감사합니다 앞으로도 일이 있을때 연락드리겠습니다', '10', '');
INSERT INTO `hellomoney`.`review` (`review_id`, `request_id`, `content`, `score`, `register_time`) VALUES ('9', '9', '먼길 오시느라 고생많으셨습니다. 대출 받는게 두번짼데 지난번에는 좀 찜찜함이 있었거든요. 이번에는 기분좋게 진행했네요. 믿음직스럽게 처리해주셔서 감사하고요. 친절한 설명 감사합니다.', '10', '');
INSERT INTO `hellomoney`.`review` (`review_id`, `request_id`, `content`, `score`, `register_time`) VALUES ('10', '10', '빠른 일처리와 정확한 금리 견적. 진행하는 내내 기분좋았습니다. 친절하신 상담사님을 만나 기분좋은 하루가 될꺼같아요. 다음에도 다시 연락하고 싶네요 ^^', '8', '');
